function toggleMobileMenu(){const t=document.querySelector(".header__nav");var e=t.querySelector(".menu-button"),n=t.querySelectorAll("A");e.addEventListener("click",function(){t.classList.toggle("header__nav_active"),document.body.classList.toggle("no-scroll")}),n.forEach(e=>{e.addEventListener("click",function(){t.classList.remove("header__nav_active"),document.body.classList.remove("no-scroll")})}),window.addEventListener("resize",function(){992<window.innerWidth&&document.body.classList.remove("no-scroll")})}function runObserver(){const t=new IntersectionObserver(function(e){e.forEach(e=>{e.isIntersecting&&(e.target.classList.contains("to-be-animated")?e.target.classList.add("faded-in"):e.target.classList.contains("languages__item")&&e.target.classList.add("active"))})},{root:null,rootMargin:"0px",threshold:1});document.querySelectorAll(".to-be-animated").forEach(e=>t.observe(e)),document.querySelectorAll(".languages__item").forEach(e=>t.observe(e))}document.addEventListener("DOMContentLoaded",function(){toggleMobileMenu(),runObserver()});