function toggleMobileMenu(){const n=document.querySelector(".header__nav");var e=n.querySelector(".menu-button"),o=n.querySelectorAll("A");e.addEventListener("click",function(){n.classList.toggle("header__nav_active"),document.body.classList.toggle("no-scroll")}),o.forEach(e=>{e.addEventListener("click",function(){n.classList.remove("header__nav_active"),document.body.classList.remove("no-scroll")})}),window.addEventListener("resize",function(){992<window.innerWidth&&document.body.classList.remove("no-scroll")})}document.addEventListener("DOMContentLoaded",function(){toggleMobileMenu()});